import { Component } from '@angular/core';
import { countryDetails } from './select-country/country.domain';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'CountryInfo';

  obj : countryDetails
 ={
    name : 'INDIA',
    countryinfo : `The National flag of India is
     a horizontal tricolor of deep saffron (kesari) at the top,
    white in the middle and dark green at the bottom in equal
    proportion. The ratio of width of the flag to its length
    is two to three. In the centre of the white band is a 
    navy blue wheel which represents the chakra.` ,
    url : 'data:image/jpeg;base64,'

  }


  onChange(ev : string)
  {
    
    //this.obj=ev;
    if(ev=='USA')
    {
      this.obj.name="USA"
      this.obj.countryinfo=`The 50 stars on the flag represent
       the 50 states of the United States of America, and the 
       13 stripes represent the thirteen British colonies that 
       declared independence from the Kingdom of Great Britain, 
      and became the first states in the U.S.`
      this.obj.url='data:image/png;base64,'   
    }
    else if(ev=='India')
    {
      this.obj.name="India"
    this.obj.countryinfo=`The National flag of India is
    a horizontal tricolor of deep saffron (kesari) at the top,
   white in the middle and dark green at the bottom in equal
   proportion. The ratio of width of the flag to its length
   is two to three. In the centre of the white band is a 
   navy blue wheel which represents the chakra.` 
    this.obj.url='data:image/jpeg;base64,'
    }
    else if(ev=='England')
    {
      this.obj.name="England"
    this.obj.countryinfo=`DescriptionThe flag of England is 
    derived from Saint George's Cross. The association of the
     red cross as an emblem of England can be traced back to 
     the Middle Ages, and it was used as a component in the 
     design of the Union Flag in 1606.`
    this.obj.url='data:image/png;base64,'
    }
     
    


    console.log(this.obj)
  }

}
